function Bisection (f, a, b, nmax, e)  fa = f(a);  fb = f(b);   % if fa & fb same sign   if (fa * fb > 0)    fprintf('a: %f\n', a);     fprintf('b: %f\n', b);     disp(fa);     disp(fb);     fprintf('function has same signs at a and b \n');  
  endif  error = b - a;   for n = 0:nmax    error = error / 2;     c = a + error;     fc = f(c);     n    c    fc    error    if (abs(error) < e)      disp("convergence");     endif    % if fa & fc diff sign     if (fa * fc < 0)      b = c;       fb = fc;     else       a = c;       fa = fc;     endif  endforendfunction    
